def main():
    singleDigitAddition()

def singleDigitAddition():
    setList = []
    for x in range(0, 10):
        for z in range(0,10):
            prob = str(z) + "+" + str(x)
            ans = str(z + x)
            if int(ans)<10:
                print(prob + "=" + ans)
                problem_tuple = (prob, ans)
                setList.append(problem_tuple)
    print(setList)
    return setList
